import React,{useContext} from 'react'
import ThemeContext from "../Context/theamContext"

const ThemeToggler=()=>{
   const [themeMode,setThemeMode]=useContext(ThemeContext)
   return(
       <div >
           <p>{themeMode ==="light"? "Turn Off":"lights On"}</p>
       </div>
   ) 
}
export default ThemeToggler;
